    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //Se vacía la tabla para no duplicar los datos y extender la tabla.
        DefaultTableModel cleanModel = (DefaultTableModel)partidasJT.getModel();
        cleanModel.setRowCount(0);
        
        try{
            //Se crea la conección
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost/mydb", "root", "");
            Statement st = con.createStatement();
            String sql = "select * from partida";
            ResultSet rs = st.executeQuery(sql);
            
            while(rs.next()){
                
                String id = String.valueOf(rs.getInt("idpartida"));
                String nombre = rs.getString("nombreJugador");
                String acumulado = String.valueOf(rs.getInt("acumulado"));
                String ronda = String.valueOf(rs.getInt("rondaAlcanzada"));
                
                String tbData[] = {id, nombre, acumulado, ronda};
                DefaultTableModel tb1Model = (DefaultTableModel)partidasJT.getModel();
                
                tb1Model.addRow(tbData);
                
            }
            
        }catch(Exception e){
            JOptionPane.showMessageDialog(this, "Ha ocurrido la excepción " + e);
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed


